class User {  int _id;  String _name;  int _score;  // konstruktor versi 1  User(this._name, this._score);  // konstruktor versi 2: konversi dari Map ke Contact  User.fromMap(Map<String, dynamic> map) {    this._id = map['id'];    this._name = map['name'];    this._score = map['score'];  }  //getter dan setter (mengambil dan mengisi data kedalam object)  // getter  int get id => _id;  String get name => _name;  int get score => _score;  // setter  set name(String value) {    _name = value;  }  set score(int value) {    _score = value;  }  // konversi dari Contact ke Map  Map<String, dynamic> toMap() {    Map<String, dynamic> map = Map<String, dynamic>();    map['id'] = this._id;    map['name'] = name;    map['score'] = score;    return map;  }}